mutation VerifyPhoneCode(
  $phoneNumber:String!
  $code:String!
) {
  verifyPhoneCode(verifyPhoneInput :{
    phoneNumber:$phoneNumber
    code:$code
  }){
      isPhoneVerified
      phoneNumber
      token
      user{
        id,
        fullName,
        emailAddress,
        phoneNumber,
        isPhoneVerified,
        role
      }
  }
}

mutation Login(
  $emailAddress:String!
  $phoneNumber:String!
  $code:String!
) {
  login(loginInput :{
    emailAddress:$emailAddress
    phoneNumber:$phoneNumber
    code:$code
  }){
      isEmailVerified
      emailAddress
      token
      user{
        id,
        fullName,
        emailAddress,
        phoneNumber,
        isPhoneVerified,
        role
      }
  }
}

mutation ResetPasswordRequestFromMobile(
  $emailAddress:String!
) {
  resetPasswordRequestFromMobile(
      resetPasswordRequestInput :{
    emailAddress:$emailAddress
  }){
      resetPasswordToken
      resetUrl
      user{
        id,
        fullName,
        emailAddress,
        phoneNumber
      }
  }
}

mutation UpdatePassword(
  $emailAddress:String!
  $password:String!
  $confirmPassword:String!
  $resetPasswordToken:String!
) {
  updatePassword(updatePasswordInput :{
    emailAddress:$emailAddress
    password:$password
    confirmPassword:$confirmPassword
    resetPasswordToken:$resetPasswordToken
  }){
      message
      user{
        id,
        fullName,
        emailAddress,
        phoneNumber,
        isPhoneVerified,
        isPendingRoleChangeRequest,
        role
      }
  }
}

