query GetAllSpecialists {
  getAllSpecialists {
        id
        fullName
        emailAddress
        phoneNumber
  }
}

mutation CreateSpecialistConsultationType(
  $types: [ConsultationTypeEnum!]!
  $availableDate: DateTime!  
  $startTime: String!
  $endTime: String!
) {
  createSpecialistConsultationType(
    createConsultationTypeInput: {
      types: $types
      availableDate: $availableDate
      startTime: $startTime
      endTime: $endTime
    }) {
        id
        specialist {
            id
            physicianID
            fullName
            emailAddress
            phoneNumber
        }
        types
        availableDate
        startTime
        endTime
        unAllocatedSlotInMinutes
        appointmentId
  }
}

query GetSpecialistCalendar(
  $startDate: DateTime!
  $endDate: DateTime!
  $specialistId: String!
) {
  getSpecialistCalendar(
    getSpecialistCalendarInput: {
      startDate: $startDate
      endDate: $endDate
      specialistId: $specialistId
    }) {
        consultationType{
        id
        specialist {
            id
            fullName
            emailAddress
            phoneNumber
        }
        types
        availableDate
        startTime
        endTime
        unAllocatedSlotInMinutes
        appointmentId
      }
      isFree
  }
}

mutation CreateGroupChatChannel(
  $description: String!
  $groupName: String!
  $maxParticipants: Float!
  $location: String!
) {
  createGroupChatChannel(
		createGroupChatChannelInput: {
      groupName: $groupName
      maxParticipants: $maxParticipants
      location:$location
      description: $description
    }
  ) {
      id
      ownerId
      moderatorId
      description
      groupName
      maxParticipants
      location
  }
}

mutation AddMemberToGroup(
  $userId: String!
  $groupId: String!
) {
  addMemberToGroup(
		addMemberToGroupInput: {
      userId: $userId
      groupId: $groupId
    }
  ) 
}
